mapper.insert:
INSERT INTO admin
VALUES (
	   null
	 , #{AD_ID}
	 , #{AD_PW}
	 , #{AD_NAME}
	 , DATE_FORMAT(NOW(), '%Y-%m-%d')
	 , 'Y'
	 , #{RL_TYPE}
)

mapper.select:
	SELECT *
	  FROM common
	 WHERE name = #{type} 
 	 ORDER BY code asc;

mapper.update:
UPDATE admin SET
AD_ACTIVE = 'Y' AD_ACTIVE = 'N'
WHERE AD_ID = #{id}

UPDATE common SET
hide = 'N' hide = 'Y'
WHERE code = #{code};

UPDATE common
   SET value = #{value}
 WHERE code = #{code}

UPDATE auction_product
   SET APD_STATUS = #{APD_STATUS}
 WHERE APD_IDX = #{APD_IDX}
