mapper.insert:
INSERT INTO admin
VALUES (
	   null
	 , #{AD_ID}
	 , #{AD_PW}
	 , #{AD_NAME}
	 , DATE_FORMAT(NOW(), '%Y-%m-%d')
	 , 'Y'
	 , #{RL_TYPE}
)

mapper.select:
WITH RECURSIVE tmp(CTG_CODE, CTG_NAME, UP_CTG_CODE, path) AS ( SELECT CTG_CODE, CTG_NAME, UP_CTG_CODE, CTG_NAME FROM category WHERE UP_CTG_CODE = 'T' UNION ALL SELECT t.CTG_CODE, t.CTG_NAME, t.UP_CTG_CODE, CONCAT(tp.path, '/', t.CTG_NAME) FROM category t INNER JOIN tmp tp ON t.UP_CTG_CODE = tp.CTG_CODE ) SELECT PD_IDX , p.US_ID , p.PD_SUBJECT , c.value AS PD_STATUS , p.PD_REG_DATE , tmp.PATH AS PD_CATEGORY FROM product p LEFT JOIN tmp ON p.PD_CATEGORY = tmp.CTG_CODE LEFT JOIN common c ON p.PD_STATUS = c.code WHERE 1 = 1
AND US_ID LIKE concat('%', #{keyword} , '%') AND PD_SUBJECT LIKE concat('%', #{keyword} , '%') AND PD_CATEGORY LIKE concat('%', #{keyword} , '%')
ORDER BY PD_REG_DATE DESC;

	SELECT *
	  FROM common
	 WHERE name = #{type} 
 	 ORDER BY code asc;

WITH RECURSIVE tmp(CTG_CODE, CTG_NAME, UP_CTG_CODE, path) AS ( SELECT CTG_CODE, CTG_NAME, UP_CTG_CODE, CTG_NAME FROM category WHERE UP_CTG_CODE = 'T' UNION ALL SELECT t.CTG_CODE, t.CTG_NAME, t.UP_CTG_CODE, CONCAT(tp.path, '/', t.CTG_NAME) FROM category t INNER JOIN tmp tp ON t.UP_CTG_CODE = tp.CTG_CODE ) SELECT ap.APD_IDX , ap.APD_OWNER , ap.APD_NAME , ap.APD_DETAIL , tmp.PATH AS APD_CATEGORY , c2.value as APD_REJECTION , ap.APD_REG_DATE , c1.value as APD_STATUS FROM auction_product ap LEFT JOIN tmp ON ap.APD_CATEGORY = tmp.CTG_CODE LEFT JOIN common c2 ON ap.APD_REJECTION = c2.code LEFT JOIN common c1 ON ap.APD_STATUS = c1.code WHERE 1 = 1
AND c.code = #{type}
ORDER BY ap.APD_REG_DATE DESC;

mapper.update:
UPDATE admin SET
AD_ACTIVE = 'Y' AD_ACTIVE = 'N'
WHERE AD_ID = #{id}

UPDATE common SET
hide = 'N' hide = 'Y'
WHERE code = #{code};

UPDATE common
   SET value = #{value}
 WHERE code = #{code}

UPDATE category
   SET CTG_NAME = #{CTG_NAME}
 WHERE CTG_CODE = #{CTG_CODE}

UPDATE category SET
CTG_HIDE = 'N' CTG_HIDE = 'Y'
WHERE CTG_CODE = #{code};

UPDATE auction_product
   SET APD_STATUS = #{APD_STATUS}
 WHERE APD_IDX = #{APD_IDX}
