     , DATE_FORMAT(DATE_ADD(NOW(), INTERVAL #{APD_DEADLINE} DAY), '%Y-%m-%d %H:%i')
     , NULL
     , #{APD_START_PRICE}
     , DEFAULT
 )

mapper.update:
UPDATE auction_product
   SET APD_STATUS = #{APD_STATUS}
      , APD_REJECTION = #{APD_REJECTION} 
 WHERE APD_IDX = #{APD_IDX}

UPDATE auction
   SET AT_MAIN = 'Y'
 WHERE APD_IDX = #{APD_IDX}

mapper.select:
	SELECT APD_IDX
	  FROM auction
  	 WHERE APD_IDX = #{APD_IDX}
