mapper.select:
	SELECT *
	  FROM common
	 WHERE name = #{type}
 	 ORDER BY code asc;

mapper.update:
UPDATE admin SET
AD_ACTIVE = 'Y' AD_ACTIVE = 'N'
WHERE AD_ID = #{id}

UPDATE common SET
hide = 'N' hide = 'Y'
WHERE code = #{target};

UPDATE common
   SET value = #{value}
 WHERE code = #{code}
