mapper.select:
SELECT US_ID , US_NAME , US_NICK , US_PHONE , US_REG_DATE FROM user WHERE
US_NAME LIKE concat('%', #{keyword} , '%') US_ID LIKE concat('%', #{keyword} , '%') US_NICK LIKE concat('%', #{keyword} , '%') 1 = 1
LIMIT #{limit.startRow}, #{limit.listLimit}

mapper.update:
UPDATE admin SET
AD_ACTIVE = 'Y' AD_ACTIVE = 'N'
WHERE AD_ID = #{target};

UPDATE common SET
hide = 'N' hide = 'Y'
WHERE code = #{target};

UPDATE common
   SET value = #{value}
 WHERE name = #{target} AND code = #{code};
