mapper.select:
SELECT code, value
FROM common
WHERE hide != 'Y' AND name='REPORT_CATEGORY';

SELECT code, value
FROM common
WHERE hide != 'Y' AND name='REVIEW_CATEGORY';

SELECT u.US_PROFILE
FROM `user` u
WHERE u.US_ID = #{US_ID}

mapper.insert:
INSERT INTO report
VALUES (
	NULL
	, #{TO_ID}
	, #{FROM_US_ID}
	, 'RS01'
	, #{RP_CATEGORY}
	, #{RP_CONTENT}
	, DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i')
	, #{RP_IMG1}
	, #{RP_IMG2}
)

INSERT INTO review
VALUES (
	NULL
	,#{TO_US_ID}
	,#{FROM_US_ID}
	,#{RV_STAR}
	,#{RV_CATEGORYS}
	,#{RV_CONTENT}
	, DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i')	
)
