mapper.select:
SELECT *, (SELECT COUNT(*) FROM community WHERE 1=1
AND CM_CATEGORY = #{type}
) AS count FROM community WHERE 1=1
AND CM_CATEGORY = #{type}
ORDER BY CM_IDX desc

mapper.insert:

	SELECT value
	FROM common c 
	WHERE code = #{CM_CATEGORY}


INSERT INTO community 
VALUES(
	NULL 
	, #{CM_ID}
	, #{CM_TITLE}
	, #{CM_CONTENT}
	, #{CM_IMAGE1}
	, #{CM_IMAGE2} 
	, #{CM_IMAGE3} 
	, now()
	, #{category}
	, #{CM_NICK}
);
