mapper.select:
SELECT CM_IDX , CM_TITLE , CM_REG_DATE , CM_CATEGORY , CM_NICK , ROW_NUMBER() OVER(ORDER BY CM_IDX) AS count FROM community cm WHERE 1=1
AND CM_CATEGORY = (SELECT value FROM common c WHERE code = #{type})
ORDER BY CM_IDX desc LIMIT #{startRow}, #{listLimit}

mapper.update:
		UPDATE community
      SET CM_READ_COUNT = CM_READ_COUNT + 1
      WHERE CM_IDX = #{CM_IDX}

mapper.insert:

	SELECT value
	FROM common c 
	WHERE code = #{CM_CATEGORY}


INSERT INTO community 
VALUES(
	NULL 
	, #{CM_ID}
	, #{CM_TITLE}
	, #{CM_CONTENT}
	, null
	, null
	, null
	, DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i')
	, #{category}
	, #{CM_NICK}
);

mapper.update:

	SELECT value
	FROM common c 
	WHERE code = #{CM_CATEGORY}

UPDATE community
SET 
	CM_CATEGORY = #{category} 
  , CM_TITLE = #{CM_TITLE}
  , CM_CONTENT = #{CM_CONTENT}
WHERE
	CM_IDX = #{CM_IDX}
