mapper.update:
UPDATE product
SET PD_STATUS = #{status}
WHERE PD_IDX = #{pdId}

UPDATE user 
SET US_VISIT = US_VISIT + 1 
WHERE US_ID = #{userId}

mapper.select:
SELECT US_VISIT 
FROM user 
WHERE US_ID = #{userId}

SELECT COUNT(*) 
FROM product
WHERE US_ID = #{userId} AND PD_STATUS = 'PDS03';
