mapper.select:
select name , code , value  
from common c 
where name = 'PD_CONDTION';

select name , code , value  
from common c 
where name = 'PD_METHOD';

select name , code , value  
from common c 
where name = 'PD_STATUS';

mapper.update:
UPDATE product
SET PD_READCOUNT = PD_READCOUNT + 1
WHERE PD_IDX = #{pd_idx}
