mapper.insert:
INSERT INTO user
VALUES (
	null
	, #{US_ID}
	, #{US_PASSWD}
	, #{US_NAME}
	, #{US_NICK}
	, #{US_EMAIL}
	, #{US_POST_CODE}
	, #{US_ADDRESS1}
	, #{US_ADDRESS2}
	, #{US_PHONE}
	, #{US_GENDER}
	, DATE_FORMAT(NOW(), '%Y-%m-%d %H:%i:%s')
	, 0
)

mapper.select:
SELECT IF(COUNT(*) = 1, 1, 0)
FROM  user 
WHERE US_ID = #{US_ID}

SELECT IF(COUNT(*) = 1, 1, 0)
FROM  user 
WHERE US_NICK = #{US_NICK}

SELECT *
  FROM user 
 WHERE
	   US_NAME =  #{user_name}  
 AND   US_EMAIL = #{user_email}

SELECT US_ID
  FROM user 
 WHERE
 		   US_NAME = #{userName}
 and	   US_PHONE = #{userPhone}

SELECT *
  FROM user 
 WHERE
	   US_Name =  #{user_name}  
 AND   US_ID = 	  #{user_id}
 AND   US_EMAIL = #{user_email}

SELECT COUNT(*)
FROM user
WHERE 	
		US_ID = #{userId}
AND		US_NAME = #{userName}
AND		US_PHONE = #{userPhone}

mapper.update:
UPDATE mail_auth_info
SET 
	auth_code = #{auth_code}
WHERE
	US_EMAIL = #{US_EMAIL}

	UPDATE user
	SET
		US_PASSWD = #{US_PASSWD}
	WHERE
		US_ID = #{US_ID}

UPDATE user
SET
US_NICK = #{value}
US_EMAIL = #{value}
US_PHONE = #{value}
WHERE US_ID = #{userId}
